						where v.ID > 0
						orderby v.ID
						select v).ToList();


					foreach (var lang in languageResourceValues)
					{
						var fixedValue = lang.Value;

						// EXPLANATION OF REGULAR EXPRESSIONS BELOW:
						// @"^\s+" finds all whitespace preceding the line and "" replaces it with an empty string
						// @"\n\s+" finds all whitespace after a newline charcter and "\n" replaces it with a newline character
						// \s considers whitespace to be: [ \t\r\n\f], i.e. space, tab, carriage return, linefeed (newline), and form feed character
						fixedValue = Regex.Replace(fixedValue, @"^\s+", m => ""); 
