		class LogManager {

			String idCode = "";
			String tempRawLogFileName = "rawlog.txt";
			String outputHtmlFileName = "log.html";
			String[] rawLogFileLines;

			public LogManager(String desiredIdCode) {
				idCode = desiredIdCode;
			}

